INSERT INTO `wp_es_pluginconfig` VALUES (1,'Admin','yo@iogrow.com','WP HTML MAIL','YES','yo@iogrow.com','io Grow Blog New email subscription','Hi Admin, \r\n\r\nWe have received a request to subscribe new email address to receive emails from our website. \r\n\r\nEmail: ###EMAIL### \r\nName : ###NAME### \r\n\r\nThank You\r\nio Grow Blog','YES','io Grow Blog Welcome to our newsletter','Hi ###NAME###, \r\n\r\nWe have received a request to subscribe this email address to receive newsletter from our website. \r\n\r\nThank You\r\nio Grow Blog','Double Opt In','io Grow Blog confirm subscription','Hi ###NAME###, \r\n\r\nA newsletter subscription request for this email address was received. Please confirm it by <a href=\'###LINK###\'>clicking here</a>. If you cannot click the link, please use the following link. \r\n\r\n ###LINK### \r\n\r\nThank You\r\nio Grow Blog','http://blog.iogrow.com/?es=optin&db=###DBID###&email=###EMAIL###&guid=###GUID###','http://blog.iogrow.com/?es=unsubscribe&db=###DBID###&email=###EMAIL###&guid=###GUID###','No longer interested email from io Grow Blog?. Please <a href=\'###LINK###\'>click here</a> to unsubscribe','Thank You, You have been successfully unsubscribed. You will no longer hear from us.','Thank You, You have been successfully subscribed to our newsletter.','Oops.. This subscription cant be completed, sorry. The email address is blocked or already subscribed. Thank you.','Oops.. We are getting some technical error. Please try again or contact admin.');
